		defaultTab: ".show_shownotes"
		*/
	});
			
	$('div[id*=shownotes] a').click(
	function ()
	{
		time=$(this).data('seconds');
		
		var p = time.split(':'),
		        seconds = 0, m = 1;
		
		    while (p.length > 0) {
		        seconds += m * parseInt(p.pop(), 10);
		        m *= 60;
		    }
		
		
		id=$(this).closest("div").attr("id").replace('shownotes','');
		/* console.log(seconds + ' ' + id); */
		
		playfrom(id, seconds);
		return false;
	}
	);
	
	

