function show_ep_ratings (num)
{
	
	$('div[id*=ep_rating_wrap_' + num + ']').slideDown(400);
	
	$('#ep_tabs_' + num + '_ratings').addClass('active');
	$('#ep_tabs_' + num + '_shownotes').removeClass('active');

}

function hide_ep_ratings (num)
{
	
	$('div[id*=ep_rating_wrap_' + num + ']').slideUp(400);
	
	$('#ep_tabs_' + num + '_ratings').removeClass('active');
	$('#ep_tabs_' + num + '_shownotes').addClass('active');

}



